mapper.select:
select 

from collection_task_time_point_status
where ctid = #{ctid,jdbcType=VARCHAR}
  and timeSave = #{timesave,jdbcType=BIGINT}
  and timeDelay = #{timedelay,jdbcType=BIGINT}

mapper.delete:
delete from collection_task_time_point_status
where ctid = #{ctid,jdbcType=VARCHAR}
  and timeSave = #{timesave,jdbcType=BIGINT}
  and timeDelay = #{timedelay,jdbcType=BIGINT}

mapper.update:
update collection_task_time_point_status


    timeCurrent = #{timecurrent,jdbcType=BIGINT},


    status = #{status,jdbcType=VARCHAR},


where ctid = #{ctid,jdbcType=VARCHAR}
  and timeSave = #{timesave,jdbcType=BIGINT}
  and timeDelay = #{timedelay,jdbcType=BIGINT}

update collection_task_time_point_status
set timeCurrent = #{timecurrent,jdbcType=BIGINT},
  status = #{status,jdbcType=VARCHAR}
where ctid = #{ctid,jdbcType=VARCHAR}
  and timeSave = #{timesave,jdbcType=BIGINT}
  and timeDelay = #{timedelay,jdbcType=BIGINT}
