mapper.select:
select convert(fdi.percent, decimal(10,2)) percent,
from storage_data_field_detail_info fdi
1=1
and fdi.id = #{id,jdbcType=VARCHAR}
and fdi.collectName = #{collectName,jdbcType=VARCHAR}
order by fdi.percent desc

mapper.update:
update storage_data_field_detail_info


    count = #{count,jdbcType=DOUBLE},


    percent = #{percent,jdbcType=DOUBLE},


where id = #{id,jdbcType=VARCHAR}
  and collectName = #{collectname,jdbcType=VARCHAR}
  and type = #{type,jdbcType=VARCHAR}
  and field = #{field,jdbcType=VARCHAR}
  and fieldType = #{fieldtype,jdbcType=SMALLINT}

update storage_data_field_detail_info
set count = #{count,jdbcType=DOUBLE},
  percent = #{percent,jdbcType=DOUBLE}
where id = #{id,jdbcType=VARCHAR}
  and collectName = #{collectname,jdbcType=VARCHAR}
  and type = #{type,jdbcType=VARCHAR}
  and field = #{field,jdbcType=VARCHAR}
  and fieldType = #{fieldtype,jdbcType=SMALLINT}
