mapper.select:
select
from storage_data_index_detail_info
1=1
and id = #{id,jdbcType=VARCHAR}
and collectName = #{collectName,jdbcType=VARCHAR}
and field = #{field,jdbcType=VARCHAR}
and fieldType in
#{item,jdbcType=SMALLINT}
and (locate(concat(', ', #{fieldsItem,jdbcType=VARCHAR}, ','), fields) != 0 or locate(concat('[', #{fieldsItem,jdbcType=VARCHAR}, ','), fields) != 0 or locate(concat(', ', #{fieldsItem,jdbcType=VARCHAR}, ']'), fields) != 0 or locate(concat('[', #{fieldsItem,jdbcType=VARCHAR}, ']'), fields) != 0)
order by vals asc

mapper.update:
update storage_data_index_detail_info


    count = #{count,jdbcType=DOUBLE},


    percent = #{percent,jdbcType=DOUBLE},


    fields = #{fields,jdbcType=LONGVARCHAR},


    time = #{time,jdbcType=BIGINT},


where id = #{id,jdbcType=VARCHAR}
  and collectName = #{collectname,jdbcType=VARCHAR}
  and field = #{field,jdbcType=VARCHAR}
  and elements = #{elements,jdbcType=VARCHAR}
  and values = #{values,jdbcType=VARCHAR}
  and fieldType = #{fieldtype,jdbcType=SMALLINT}

update storage_data_index_detail_info
set count = #{count,jdbcType=DOUBLE},
  percent = #{percent,jdbcType=DOUBLE},
  fields = #{fields,jdbcType=LONGVARCHAR},
  time = #{time,jdbcType=BIGINT}
where id = #{id,jdbcType=VARCHAR}
  and collectName = #{collectname,jdbcType=VARCHAR}
  and field = #{field,jdbcType=VARCHAR}
  and elements = #{elements,jdbcType=VARCHAR}
  and values = #{values,jdbcType=VARCHAR}
  and fieldType = #{fieldtype,jdbcType=SMALLINT}
